const blogPosts = [
  {
    title: "Welcome to Gas Town",
    url: "https://steve-yegge.medium.com/welcome-to-gas-town-4f25ee16dd04",
    description: "Introduction to Gas Town - the new IDE paradigm for 2026"
  },
  {
    title: "The Future of Coding Agents",
    url: "https://steve-yegge.medium.com/the-future-of-coding-agents-e9451a84207c",
    description: "Where AI coding agents are headed"
  },
  {
    title: "BAGS and the Creator Economy",
    url: "https://steve-yegge.medium.com/bags-and-the-creator-economy-249b924a621a",
    description: "Building Agent-Generated Software"
  },
  {
    title: "Gas Town Emergency User Manual",
    url: "https://steve-yegge.medium.com/gas-town-emergency-user-manual-cf0e4556d74b",
    description: "Quick start guide to using Gas Town"
  },
  {
    title: "Stevey's Birthday Blog",
    url: "https://steve-yegge.medium.com/steveys-birthday-blog-34f437139cb5",
    description: "Reflections and updates from Steve"
  }
];
---

<html lang="en">
  <head>
    <meta charset="utf-8" />
    <link rel="icon" type="image/svg+xml" href="/favicon.svg" />
    <meta name="viewport" content="width=device-width" />
    <meta name="generator" content={Astro.generator} />
    <meta name="description" content="Gas Town Hall - Orchestrate your AI coding agents" />
    <title>Gas Town Hall</title>
    <style>
      :root {
        --color-bg: #0f0f0f;
        --color-surface: #1a1a1a;
        --color-border: #2a2a2a;
        --color-text: #e0e0e0;
        --color-text-muted: #888;
        --color-accent: #ff6b35;
        --color-accent-hover: #ff8555;
        --color-discord: #5865f2;
        --color-discord-hover: #6b75f5;
      }

      * {
        margin: 0;
        padding: 0;
        box-sizing: border-box;
      }

      body {
        font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, sans-serif;
        background: var(--color-bg);
        color: var(--color-text);
        line-height: 1.6;
        min-height: 100vh;
      }

      nav {
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding: 1.5rem 2rem;
        border-bottom: 1px solid var(--color-border);
        max-width: 1200px;
        margin: 0 auto;
      }

      .logo {
        font-size: 1.25rem;
        font-weight: 700;
        color: var(--color-text);
        text-decoration: none;
      }

      .nav-links {
        display: flex;
        gap: 2rem;
        align-items: center;
      }

      .nav-links a {
        color: var(--color-text-muted);
        text-decoration: none;
        font-size: 0.95rem;
        transition: color 0.2s;
      }

      .nav-links a:hover {
        color: var(--color-text);
      }

      .hero {
        max-width: 900px;
        margin: 0 auto;
        padding: 6rem 2rem 4rem;
        text-align: center;
      }

      .hero h1 {
        font-size: clamp(2rem, 5vw, 3.5rem);
        font-weight: 700;
        line-height: 1.2;
        margin-bottom: 1.5rem;
        color: var(--color-text);
      }

      .hero h1 span {
        color: var(--color-accent);
      }

      .hero p {
        font-size: 1.25rem;
        color: var(--color-text-muted);
        max-width: 600px;
