---
import BaseLayout from '../layouts/BaseLayout.astro';

const title = 'Home';
---

<BaseLayout title={title}>
  <div class="home">
    <h1>Gas Town</h1>
    <p>Multi-agent orchestration for AI development.</p>
    <a href="/docs" class="cta">View Documentation</a>
  </div>
</BaseLayout>

<style>
  .home {
    text-align: center;
    padding: 4rem 2rem;
  }

  .home h1 {
    font-size: 4rem;
    margin-bottom: 1rem;
  }

  .home p {
    font-size: 1.5rem;
    color: var(--color-text-muted);
    margin-bottom: 2rem;
  }

  .cta {
    display: inline-block;
    background-color: var(--color-accent);
    color: var(--color-bg);
    padding: 1rem 2rem;
    border-radius: 8px;
    font-weight: 600;
    font-size: 1.1rem;
  }

  .cta:hover {
    opacity: 0.9;
    text-decoration: none;
  }
</style>

