#!/usr/bin/env node

/**
 * Docs Shredder - Generates Astro content collection pages from gastown-docs
 *
 * Reads markdown files from docs-fodder/gastown-docs/ and generates
 * corresponding pages in src/content/docs/ with proper frontmatter.
 *
 * Idempotent: Safe to re-run when source docs evolve.
 */

import { readdir, readFile, writeFile, mkdir, rm } from 'fs/promises';
import { join, dirname, basename, relative } from 'path';
import { existsSync } from 'fs';
import { fileURLToPath } from 'url';

const __dirname = dirname(fileURLToPath(import.meta.url));
const PROJECT_ROOT = join(__dirname, '..', '..');
const DOCS_SOURCE = join(PROJECT_ROOT, 'docs-fodder', 'gastown-docs');
const DOCS_DEST = join(__dirname, '..', 'src', 'content', 'docs');

/**
 * Extract title from markdown content (first H1 heading)
 */
function extractTitle(content, filename) {
  const match = content.match(/^#\s+(.+)$/m);
  if (match) {
    return match[1].trim();
  }
  // Fallback to filename without extension
  return basename(filename, '.md')
    .replace(/-/g, ' ')
    .replace(/\b\w/g, c => c.toUpperCase());
}

/**
 * Generate slug from filepath relative to docs source
 */
function generateSlug(filepath) {
  const rel = relative(DOCS_SOURCE, filepath);
  return rel.replace(/\.md$/, '').toLowerCase().replace(/\s+/g, '-');
}

/**
 * Determine category from filepath
 */
function getCategory(filepath) {
  const rel = relative(DOCS_SOURCE, filepath);
  const parts = rel.split('/');
  if (parts.length > 1) {
    return parts[0];
  }
  return 'general';
}

/**
 * Get sort order for a doc based on category and filename
 */
function getSortOrder(filepath, category) {
  const filename = basename(filepath, '.md').toLowerCase();

  // Priority files come first
  const priorityFiles = ['overview', 'installing', 'reference', 'glossary'];
  const priorityIndex = priorityFiles.indexOf(filename);
  if (priorityIndex !== -1) {
    return priorityIndex;
  }

  // Category-based ordering
  const categoryOrder = {
    'general': 0,
    'concepts': 100,
    'design': 200,
    'examples': 300
  };

  return (categoryOrder[category] || 500) + 50;
}

/**
 * Process a single markdown file
 */
async function processFile(filepath) {
  const content = await readFile(filepath, 'utf-8');
  const title = extractTitle(content, filepath);
  const slug = generateSlug(filepath);
  const category = getCategory(filepath);
  const order = getSortOrder(filepath, category);

  // Generate frontmatter
  const frontmatter = [
    '---',
    `title: "${title.replace(/"/g, '\\"')}"`,
    `slug: "${slug}"`,
    `category: "${category}"`,
    `order: ${order}`,
    `source: "${relative(PROJECT_ROOT, filepath)}"`,
    '---',
    ''
  ].join('\n');

  // Combine frontmatter with content
  const processedContent = frontmatter + content;

  return {
    slug,
    category,
    title,
    order,
    content: processedContent
  };
}

/**
 * Recursively find all markdown files
 */
async function findMarkdownFiles(dir) {
  const files = [];
  const entries = await readdir(dir, { withFileTypes: true });

  for (const entry of entries) {
    const fullPath = join(dir, entry.name);
    if (entry.isDirectory()) {
      files.push(...await findMarkdownFiles(fullPath));
    } else if (entry.name.endsWith('.md')) {
      files.push(fullPath);
    }
  }

  return files;
}

/**
 * Main shredder function
 */
async function shred() {
  console.log('Docs Shredder starting...');
  console.log(`Source: ${DOCS_SOURCE}`);
  console.log(`Destination: ${DOCS_DEST}`);

  // Verify source exists
  if (!existsSync(DOCS_SOURCE)) {
    console.error(`Source directory not found: ${DOCS_SOURCE}`);
    process.exit(1);
  }

  // Clean destination directory for idempotent behavior
  if (existsSync(DOCS_DEST)) {
    await rm(DOCS_DEST, { recursive: true });
  }
  await mkdir(DOCS_DEST, { recursive: true });

  // Find all markdown files
  const files = await findMarkdownFiles(DOCS_SOURCE);
  console.log(`Found ${files.length} markdown files`);

  // Process each file
  const processed = [];
  for (const filepath of files) {
    const result = await processFile(filepath);

    // Determine output path (flatten structure but preserve category prefix)
    const outputFilename = result.slug.replace(/\//g, '-') + '.md';
    const outputPath = join(DOCS_DEST, outputFilename);

    // Ensure directory exists
    await mkdir(dirname(outputPath), { recursive: true });

    // Write processed file
    await writeFile(outputPath, result.content);

    processed.push({
      source: relative(PROJECT_ROOT, filepath),
      dest: relative(PROJECT_ROOT, outputPath),
      title: result.title,
      slug: result.slug,
      category: result.category,
      order: result.order
    });

    console.log(`  ✓ ${result.slug}`);
  }

  // Generate index file with all docs metadata
  const indexContent = JSON.stringify(processed, null, 2);
  await writeFile(join(DOCS_DEST, '_index.json'), indexContent);

  console.log(`\nShredded ${processed.length} docs`);
  console.log('Index written to _index.json');

  // Print summary by category
  const byCategory = processed.reduce((acc, doc) => {
    acc[doc.category] = (acc[doc.category] || 0) + 1;
    return acc;
  }, {});

  console.log('\nBy category:');
  for (const [cat, count] of Object.entries(byCategory)) {
    console.log(`  ${cat}: ${count}`);
  }
}

// Run
shred().catch(err => {
  console.error('Shredder failed:', err);
  process.exit(1);
});

